import { Outlet, Navigate } from 'react-router-dom';
import axios from 'axios'
import { useEffect, useState } from 'react';
import {getUser} from '../adapters/user.js'

const PrivateRoute = () => {
  const [isTokenValid, setIsTokenValid] = useState(null);

  useEffect(() => {
    (async function () {
      const authToken = localStorage.getItem('authToken');
      const api = `${process.env.REACT_APP_SEVER_IP}:${process.env.REACT_APP_SERVER_PORT}/api/token`;

      try {
        const response = await axios.post(api, { authToken });

        if (response.data === 'Token was dead') {
          setIsTokenValid(false);
        } else {
          localStorage.setItem("userAuth", response.data)
          setIsTokenValid(true);
        }
      } catch (error) {
        console.error('Error checking token:', error);
        setIsTokenValid(false);
      }
    })();
  }, []);

  if (isTokenValid === null) {
    return null;
  }

  return isTokenValid ? <Outlet /> : <Navigate to="/login" />;
};

export default PrivateRoute;
