import React, { useState, useEffect, useRef, useContext } from "react";
import PostComponent from "./Post";
import { getPosts } from "../services/post.service";
import { UserContext } from "../contexts/UserContext";
import InfiniteScroll from "react-infinite-scroll-component";
import socket from "../socket/socket-client";

export default function ListPost() {
    const user = useContext(UserContext);
    const [posts, setPosts] = useState([]);
    const [hasMore, setHasMore] = useState(false)

    const maxPost = useRef(0);
    const limit = 6;
    const offset = 0;

    useEffect(() => {
        fetchMoreData()
    }, [])

    useEffect(() => {
        console.log(posts)
    }, [posts])

    useEffect(() => {

        function handlePostAdded(newPost) {

            const newList = [newPost, ...posts]

            setPosts(newList)
        }

        function handleCommentAdded(newComment) {
            setPosts((prevPost) => {
                const updatedPost = prevPost.map((postItem) => {
                    if (postItem._id === newComment.postID) {    
                        // Nếu postID trùng khớp với postItem._id, chèn newComment vào đầu mảng comments
                        const updatedComments = [newComment, ...postItem.comments];
                        return {
                            ...postItem,
                            comments: updatedComments,
                        };
                    }
                    return postItem;
                });
                return updatedPost;
            });
        }

        socket.on("postAdded", handlePostAdded)
        socket.on("commentAdded", handleCommentAdded)

        return () => {
            socket.off("postAdded", handlePostAdded)
            socket.off("commentAdded", handleCommentAdded)
        }

    }, []);

    async function fetchMoreData() {
        const payload = await getPosts(limit, offset + maxPost.current)
       
        if (payload.data.length > 0) {
            setPosts([...posts, ...payload.data])
            maxPost.current += payload.data.length
            setHasMore(true)
        } else {
            setHasMore(false)
        }
    }

    return (
        <InfiniteScroll
            dataLength={posts.length}
            next={fetchMoreData}
            hasMore={hasMore}
            loader={<h4>Loading...</h4>}
            scrollableTarget="content"
        >
            
        </InfiniteScroll>
    );
}
