import { BrowserRouter, Routes, Route } from "react-router-dom";
import HomePage from "./pages/HomePage.jsx";
import Login from "./pages/LoginPage.jsx"
import Register from "./pages/RegisterPage.jsx"
import Chat from "./pages/ChatPage.jsx";
import Profile from "./pages/ProfilePage.jsx";
import PrivateRoute from "./config/PrivateRoute.js";
import { useState } from "react";
import { UserProvider } from "./contexts/UserContext.js";
import AlertForm from "./components/AlertForm.jsx";

function App() {
  const [loading, setLoading] = useState(true);
  const [userData, setUserData] = useState({});
  // const [alertContent, setAlertContent] = useState({ code: 200, message: 'Upload successfully' });
  // const [stateAlert, setStateAlert] = useState(true)
  
  const fetchData = async () => {
    try {
      const response = await getUser(id);
      if (response.code === 200) {
        setUserData(response.results);
        setLoading(false);
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
      setLoading(true);
    }
  };

  return (
    <>
      <UserProvider trigger={trigger}>
        <BrowserRouter>
          <Routes>
            <Route element={<PrivateRoute fetchData={fetchData} />}>
              <Route element={<HomePage />} path="/" />
              <Route element={<Chat />} path="/chat" />
              <Route element={<Profile />} path="/profile" />
            </Route>

            <Route element={<Login />} path="/login" />
            <Route element={<Register />} path="/register" />
          </Routes>
        </BrowserRouter>
      </UserProvider>

      {/* <AlertForm
        stateAlert={stateAlert}
        setStateAlert={(stateAlert) => { setStateAlert(stateAlert) }}
        code={alertContent.code}
        message={alertContent.message}
      /> */}
      </>
  );
}

export default App;
