import { useContext, useState } from "react";
import MusicService from "../services/music.service";
import { UserContext } from "../contexts/UserContext";
import Resizer from 'react-image-file-resizer';
import ManualMusicUploadForm from "../forms/manual_music_upload.form";
import YoutubeMusicUploadForm from "../forms/automatic_music_upload.form";

/**
 * Notifications (warnings)
 * @param {object} onPopup true: MusicPlayerForm will be show, otherwise
 * @param {object} onAlert { code, message }
 * @param {function} onState true (show alert) or false (hidden alert)
 */
export default function MusicPlayerForm({ onPopup, onAlert, onState }) {
   

    // Hình thức upload nhạc
    const [isChooseLinkYoutube, setIsChooseLinkYoutube] = useState(true)
    const [isChooseManual, setIsChooseManual] = useState(false)

    const user = useContext(UserContext);

   

    return (
        <div className="modalForm">
         {
            (isChooseManual) ? <ManualMusicUploadForm onPopup={onPopup} onAlert={onAlert} onState={onState} /> : <YoutubeMusicUploadForm />
         }
        </div>
    )
}
