import { useContext, useState } from "react";
import MusicService from "../services/music.service";
import { UserContext } from "../contexts/UserContext";
import Resizer from 'react-image-file-resizer';

import "../styles/musicPlayerForm.css"
/**
 * Notifications (warnings)
 * @param {object} onPopup true: MusicPlayerForm will be show, otherwise
 * @param {object} onAlert { code, message }
 * @param {function} onState true (show alert) or false (hidden alert)
 */
export default function ProfileForm({ onPopup, onAlert, onState }) {
    const [firstName, setFirstName] = useState("");
    const [isFirstNameValid, setIsFirstNameValid] = useState(true);

    const [lastName, setLastName] = useState("");
    const [isLastNameValid, setIsLastNameValid] = useState(true);

    const [avatar, setAvatar] = useState(null);
    const [isAvatarValid, setIsAvatarValid] = useState(true);

    const [bio, setBio] = useState("");
    const [isBioValid, setIsBioValid] = useState(true);

