    }

    return (
        <div className="musicPlayerForm">
            <h2>Edit Profile</h2>

            <div>
                <div className="flex">
                    <span className="step">1</span>
                    <p>Change First Name</p>
                </div>
                <div className="row justify-content-space-between">
                    <div>
                        <input maxLength={24} type="text" name="firstName" placeholder="Ex: Điều cha chưa nói" value={firstName} onChange={(e) => aaaa(e)} />
                        <label className={`mt-5 ${isFirstNameValid ? 'd-none' : 'd-block'}`}>⚠️ Please enter name song valid</label>
                    </div>
                    <button className="btn">Change this</button>
                </div>
            </div>

            <div>
                <div className="flex">
                    <span className="step">2</span>
                    <p>Change Last Name</p>
