                <label className={`mt-5 ${isLastNameValid ? 'd-none' : 'd-block'}`}>⚠️ Please enter author name valid</label>
            </div>

            <div>
                <div className="flex">
                    <span className="step">3</span>
                    <p>Upload image</p>
                </div>
                <div className="uploadButton">
                    <input
                        type="file"
                        id="actual-btn-image"
                        hidden
                        onChange={handleImageChange}
                        accept="image/png, image/gif, image/jpeg"
                    />
                    <label htmlFor="actual-btn-image">Choose File</label>
                </div>
            </div>
        </div>
    )
}
