import { useNavigate } from "react-router-dom";
import { useUserContext } from '../contexts/UserContext.js';

const generalAction = {
    edirectToProfile() {
        const { user } = useUserContext();
        const navigate = useNavigate();
        navigate("/profile/" + user._id);
    }
}

export default generalAction
