import React, { useEffect, useRef, useState } from 'react';
import PostList from '../components/PostList';
import HandlePost from '../components/HandlePost';
import NavigationBar from '../components/NavigationBar';
import Header from '../components/Header';
import Stories from '../components/Stories';
import ListFriends from '../components/ListFriends';
import socket from '../socket/socket-client';

export default function HomePage() {
    const [friends, setFriends] = useState([])
    const userRef = useRef(localStorage.getItem("authToken"));

    useEffect(() => {
        socket.emit("getFriends", userRef.current)
    
        function onGetFriends(friends) {
          setFriends(friends)
        }
        socket.on("getFriends", onGetFriends)
    
        return () => {
          socket.off('getFriends', onGetFriends);
        };
    }, [])

    return (
        <div id="app">
            <NavigationBar />
            {/* WRAPPER */}
            <div className='flex-1'>
                {/* HEADER */}
                <Header title="Homepage" isSearchBar={true} />
                {/* WRAP CONTENT */}
                <div id='wrap-content-homepage' className="row">
                    <div id="content">
                        {/* STORIES */}
                        <Stories />

                        {/* HANDLE POST */}
                        <HandlePost />
                    
                        {/* NƠI ĐẶT POST DO NGƯỜI DÙNG ĐĂNG */}
                        <PostList />
       
                    </div>
                    {/* RIGHT SIDEBAR */}
                    <ListFriends friends={friends} />
                </div>
            </div>
        </div>
    );
}
