import { useContext, useEffect, useState } from "react"
import MusicService from "../services/music.service"
import "../styles/musicPlayerBar.css"
import { UserContext } from "../contexts/UserContext";

export default function MusicPlayerBar() {
    const [song, setSong] = useState(null);
    const user = useContext(UserContext)
    const resource = `${process.env.REACT_APP_SERVER_SERVICE}/musics/${user._id}/`

    useEffect(() => {
        MusicService.getMusicByOwnerId(user._id).then((payload) => {
            if (payload.code === 200) {
                setSong(payload.data)
            }
        })
    }, [])

    return (
        <div id="musicPlayerBar">
            <div>
                <img c src="https://img.icons8.com/ios-glyphs/30/play--v1.png" alt="play--v1"/>
            </div>
        </div>
    )
}
