import { useContext, useState } from "react";
import MusicService from "../services/music.service";
import { UserContext } from "../contexts/UserContext";

import "../styles/musicPlayerForm.css"
/**
 * Notifications (warnings)
 * @param {object} onPopup true: MusicPlayerForm will be show, otherwise
 * @param {object} onAlert { code, message }
 * @param {function} onState true (show alert) or false (hidden alert)
 */
export default function MusicPlayerForm({ onPopup, onAlert, onState }) {
    const [nameSong, setNameSong] = useState("")
    const [author, setAuthor] = useState("")
    const [isSongNameValid, setIsSongNameValid] = useState(true)
    const [isAuthorValid, setIsAuthorValid] = useState(true)
    const [music, setMusic] = useState("")
    const user = useContext(UserContext);
    const [fileName, setFileName] = useState('No file chosen');
    const [songImage, setSongImage] = useState('No file chosen');
     
    const handleFileChange = (e) => {
        const fileInput = e.target;
        if (fileInput.files.length > 0) {
            setFileName(fileInput.files[0].name);
            setMusic(fileInput.files[0])
        } else {
            setFileName('No file chosen');
        }
    };

    const handleImageChange = (e) => {
        const fileInput = e.target;
        if (fileInput.files.length > 0) {
            
        } else {
            setSongImage('No file chosen');
        }
    }

    const handleUploadClick = async () => {
        if (nameSong.trim() == "") {
            return
        }
        if (author.trim() == "") {
            return
        }
        if (music == "") {
            return
        }

        const infoSong = {
            'name': nameSong,
            'author': author,
            'owner': user._id,
            'creationtime': new Date()
        }

        // Add music to database     
        const payload = await MusicService.createMusic(infoSong, music) 

        onPopup(false);
        onAlert({ code: payload.code, message: payload.message });
        onState(true);

        resetForm();
    };

    const resetForm = () => {
        setNameSong("")
        setAuthor("")
        setFileName('No file chosen')
    }

    return (
        <div className="musicPlayerForm">
            <h2>Add music</h2>
            <div>
                <div className="flex">
                    <span className="step">1</span>
                    <p>Enter name song</p>
                </div>
                <input type="text" placeholder="Ex: Điều cha chưa nói" value={nameSong} onChange={(e) => setNameSong(e.target.value)} />
                <label className={`mt-5 ${isSongNameValid ? 'opacity-0' : 'opacity-1'}`}>⚠️ Please enter name song valid</label>
            </div>

            <div>
                <div className="flex">
                    <span className="step">2</span>
                    <p>Enter author</p>
                </div>
                <input type="text" placeholder="Ex: Ali Hoàng Dương" value={author} onChange={(e) => setAuthor(e.target.value)} />
                <label className={`mt-5 ${isAuthorValid ? 'opacity-0' : 'opacity-1'}`}>⚠️ Please enter author name valid</label>
            </div>

            <div>
                <div className="flex">
                    <span className="step">3</span>
                    <p>Upload your song</p>
                </div>
                <div className="uploadButton">
                    <input
                        type="file"
                        id="actual-btn-song"
                        hidden
                        onChange={handleFileChange}
                        accept=".mp3, .wav, .flac"
                    />
                    <label htmlFor="actual-btn-song">Choose File</label>
                    <span id="file-chosen">{fileName}</span>
                </div>
            </div>

            <div>
                <div className="flex">
                    <span className="step">4</span>
                    <p>Upload image</p>
                </div>
                <div className="uploadButton">
                    <input
                        type="file"
                        id="actual-btn-image"
                        hidden
                        onChange={handleImageChange}
                        accept="image/png, image/gif, image/jpeg"
                    />
                    <label htmlFor="actual-btn-image">Choose File</label>
                    <span id="file-chosen">{songImage}</span>
                </div>
            </div>

            <div className="flex justify-space-center">
                <button className="btn btn-black" onClick={handleUploadClick}>Upload Music</button>
            </div>
        </div>
    )
}
