import "../styles/musicPlayerForm.css"
import UploadMusicButton from "./UploadMusicButton"
import { useState } from "react";
import { sendMusic } from "../adapters/music";
/**
 * Notifications (warnings)
 * @param {object} onPopup true: MusicPlayerForm will be show, otherwise
 * @param {object} onAlert { code, message }
 * @param {function} onState true (show alert) or false (hidden alert)
 */
export default function MusicPlayerForm({ onPopup, onAlert, onState }) {
    const [isFileSelected, setIsFileSelected] = useState(false);
    const [nameSong, setNameSong] = useState("")
    const [author, setAuthor] = useState("")
    const [isSongNameValid, setIsSongNameValid] = useState(true)
    const [isAuthorValid, setIsAuthorValid] = useState(true)
    const [music, setMusic] = useState("")

    const handleUploadClick = async () => {
        if (nameSong.trim() == "") {
            return
        }
        if (author.trim() == "") {
            return
        }
        if (!isFileSelected) {
            return
        }
        
        const infoSong = {
            'name': nameSong,
            'author': author
        }
        // response.status = 200 (success), 500 (failed)
        const response = await sendMusic(music, infoSong)
        // hidden modal
        onPopup(false)
        onAlert({
            code: response.status,
            message: response.data
        })
        onState(true)
    };

    return (
        <div className="musicPlayerForm">
            <h2>Add music</h2>
            <div>
                <div className="flex">
                    <span className="step">1</span>
                    <p>Enter name song</p>
                </div>
                <input type="text" placeholder="Ex: Điều cha chưa nói" value={nameSong} onChange={(e) => setNameSong(e.target.value)}/>
                <label className={`mt-5 ${isSongNameValid ? 'opacity-0' : 'opacity-1'}`}>⚠️ Please enter name song valid</label>
            </div>

            <div>
                <div className="flex">
                    <span className="step">2</span>
                    <p>Enter author</p>
                </div>
                <input type="text" placeholder="Ex: Ali Hoàng Dương" value={author} onChange={(e) => setAuthor(e.target.value)}/>
                <label className={`mt-5 ${isAuthorValid ? 'opacity-0' : 'opacity-1'}`}>⚠️ Please enter author name valid</label>
            </div>

            <div>
                <div className="flex">
                    <span className="step">3</span>
                    <p>Upload your song</p>
                </div>
                <UploadMusicButton setMusic={(music) => {setMusic(music)}} onFileSelect={(selected) => setIsFileSelected(selected)}  />
            </div>

            <div className="flex justify-space-center">
                <button className="btn btn-black" onClick={handleUploadClick}>Upload Music</button>
            </div>
        </div>
    )
}
