import { useEffect, useRef } from "react"
import "../styles/alertForm.css"

export default function AlertForm({onState, isAlert, code, message}) {
    const timeHiddenRef = useRef(2000) // miliseconds
    const alertFormRef = useRef()

    useEffect(() => {

        if (isAlert) {
            setTimeout(() => {

                alertFormRef.current.classList.replace("d-block", "d-none")
                onState(false)
            }, timeHiddenRef.current)
        }
    }, [isAlert])

    return (
        <div ref={alertFormRef} id="alertForm" className={ (isAlert) ? "d-block" : "d-none" }>

        </div>
    )
}
