import { useState } from 'react';
import TextareaAutosize from 'react-textarea-autosize';
// https://www.w3schools.com/charsets/ref_emoji_smileys.asp

export default function EditPost() {
    const [caption, setCaption] = useState("");
    const [isActiveAccessiblity, setIsAccessiblity] = useState(false)
    const maxLength = 2200

    const MediaResource = () => {
        return (
            <div className="media" style={{
                backgroundImage: "url(https://hoanghamobile.com/tin-tuc/wp-content/uploads/2023/08/Hinh-nen-anime-cute-8-1.jpg)"
            }}>
            </div>
        )
    }

    const Accessiblity = () => {
        return (
            <>
                <p className='modal-text__sub mt-10'>Alt text describes your photos for people with visual impairments. Alt text will be automatically created for your photos or you can choose to write your own.</p>
                <div className='row align-item-center mt-5'>
                    <img width={50} height={50} src="https://hoanghamobile.com/tin-tuc/wp-content/uploads/2023/08/Hinh-nen-anime-cute-8-1.jpg"></img>
                    <input className='ml-10' type="text" placeholder='Write alt text...' />
                </div>
            </>
        )
    }
    
    return (
        <div className="modal">
            <div className="modal-header">
                <span class="material-symbols-outlined">arrow_back</span>
                <h5 class="modal-title">Edit This Post</h5>
                <b className="modal-text__main">Finish</b>
            </div>
            <div className="modal-body">
                <MediaResource />
                <div className="modal-content">
                    <div className="row align-items-center">
                        <img className="avatar" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQwOP2t8oedhQa5_OhpfY-TVIAU0gKmPV49RIWdlDuwFu2GNXDaCdtj3UIQThkMU3KNFlI&usqp=CAU"></img>
                        <p className='author'>quocthang.nevi</p>
                    </div>
                    <textarea maxLength={maxLength} value={caption} onChange={(e) => { setCaption(e.target.value) }} className='modal-textarea' placeholder='Write a captions'></textarea>
                    <div className='row justify-space-between'>
                        <span class="material-symbols-outlined">sentiment_satisfied</span>
                        <p className='modal-sub'>{caption.length} / {maxLength}</p>
                    </div>
                    <div>
                        <div onClick={ () => { setIsAccessiblity(!isActiveAccessiblity) } } className='row justify-content-space-between'>
                            <p>Add location</p>
                            <span class="material-symbols-outlined">location_on</span>
                        </div>
                        { isActiveAccessiblity ? <Accessiblity /> : ""}
                    </div>
                </div>
            </div>
        </div>
    )
}
