import axios from 'axios';

const endpoint = `${process.env.REACT_APP_SERVER_SERVICE}/api/users`;

export const getUser = async (token, id) => {
    try {
        let res = await axios.get(`${endpoint}/${id}`, {
            headers: {
                Authorization: token
            }
        });
        return res.data;
    } catch (err) {
        console.log(err);
    }
}

export const deleteUser = async (token, id) => {
    try {
        let res = await axios.delete(`${endpoint}/${id}`, {
            headers: {
                Authorization: token
            }
        });
        return res.data;
    } catch (err) {
        console.log(err);
    }
}
