import { useContext, useRef } from 'react';
import { UserContext } from '../contexts/UserContext';
import { SearchComponent, PickerComponent, StoreComponent, UnifiedComponent } from 'stipop-react-sdk'

/**
 * @param {function} showBoxReview: function hiển thị box review (nếu có) 
 * @param {function} setMedia: File object
 * @param {Ref} isActive: Trạng thái kích hoạt button khác, nếu nó là true thì không cho phép click vào button này
 * @returns 
 */
export default function StickerButton({ isActive, setSticker, showBoxReview }) {
    const stickerBoxRef = useRef(null)
    const storeBoxRef = useRef(null)
    const apiKey = '8dc984bd794a2b8e91be88e32d377699'
    const user = useContext(UserContext)

    async function openStore() {
        storeBoxRef.current.style.display = 'block'
        stickerBoxRef.current.style.display = 'none'
    }

    function openSticker() {

    }

    return (
        <div>
            <img
                className="icon-heading"
                src="https://img.icons8.com/external-icongeek26-linear-colour-icongeek26/64/external-Stickers-printing-icongeek26-linear-colour-icongeek26.png"
                alt="external-Stickers-printing-icongeek26-linear-colour-icongeek26"
                onClick={() => { !isActive.current && openSticker() }}
            />
            <div id='sticker-box'>
                <PickerComponent ref={stickerBoxRef}

                    params={{
                        apikey: apiKey,
                        userId: user._id,
                    }}

                    storeClick={() => openStore()} // true

                    stickerClick={(url) => console.log(url)}
                />

                <StoreComponent ref={storeBoxRef}
                                style={
                                    {
                                        display: 'none',
                                    }
                                }
                    params={{
                        apikey: apiKey,
                        userId: user._id,
                    }}
                    downloadParams={{
                        isPurchase: 'N',
                    }}
                />
            </div>
        </div>
    );
}
