import React, { createContext, useContext, useRef } from 'react';

class AudioContextClass {
  constructor() {
    this.audio = new Audio();
    this.playing = false;
    this.duration = 0;
    this.albums = [];
    this.currentIndex = 0;
  }

  setAlbums(albums) {
    this.albums = albums;
  }

  nextSong() {
    this.currentIndex++;
    this.play();
  }

  setDuration(duration) {
    this.duration = duration;
  }

  play(id) {
    if (id) {
      this.currentIndex = this.albums.findIndex((album) => album._id === id);
    }

    if (this.playing) {
      this.audio.pause();
    }

    this.audio.src = 
      process.env.REACT_APP_SERVER_SERVICE + "/musics/" +
      this.albums[this.currentIndex].albumId + "/" +
      this.albums[this.currentIndex].file;
 
    this.audio.play();
    this.playing = true;
  }

  pause() {
    this.audio.pause();
    this.playing = false;
  }
}

const AudioContext = createContext();

export const AudioProvider = ({ children }) => {
  const audioContextRef = useRef(new AudioContextClass());

  return (
    <AudioContext.Provider value={audioContextRef.current}>
      {children}
    </AudioContext.Provider>
  );
};

export const useAudioContext = () => {
  const context = useContext(AudioContext);
  if (!context) {
    throw new Error('useAudioContext must be used within an AudioProvider');
  }
  return context;
};
