import { React, useContext, useEffect, useRef, useState } from "react";

// COMPONENTS
import Header from "../components/Header";
import NavigationBar from "../components/NavigationBar";
import PostProfile from "../components/PostProfile";
import MusicPlayerBar from "../components/MusicPlayerBar";
import InfiniteScroll from "react-infinite-scroll-component";
import MusicPlayerForm from "../components/MusicPlayerForm";
import AlertForm from "../components/AlertForm";

import "../styles/profile.css"
// import AlertForm from "../components/AlertForm";
import { UserContext } from "../contexts/UserContext";
import { getPostByUserId } from "../services/post.service";

export default function Profile() {
    // State variables
    const [activeTab, setActiveTab] = useState('Posts');
    const [posts, setPosts] = useState([]);
    const [hasMore, setHasMore] = useState(true);
    const [isPopup, setIsPopup] = useState(false);
    const [modalForm, setModalForm] = useState("");
    const [alertContent, setAlertContent] = useState({ code: 200, message: 'Upload successfully' });
    const [stateAlert, setStateAlert] = useState(false)

    // Ref variables
    const user = useContext(UserContext);
    const limit = 6
    const [offset, setOffset] = useState(0)
    const domain = process.env.REACT_APP_SERVER_SERVICE

    useEffect(() => {
        setPosts([])
        setOffset(0)
        setHasMore(true)
    
        switch (activeTab) {
            case 'Posts':
                fetchMoreData()
        
                break;
            case 'Music':

                break;
            default:
                break;
        }
    }, [activeTab]);

    const handleNavigation = (selectedTab) => {
        setActiveTab(selectedTab);
    };

    async function fetchMoreData() {
        let newPosts = []

        switch (activeTab) {
            case 'Posts':
                const payload = await getPostByUserId(user._id, limit, offset);
                newPosts = payload.data
                break;
            case 'Music':
                break;
            default:
                break;
        }

        if (newPosts.length < limit) {
            setHasMore(false)
        }

        setPosts((prevPosts) => [...prevPosts, ...newPosts]);
        setOffset((prevOffset) => prevOffset + newPosts.length);
    }

    function handleUpload() {
        setIsPopup(true)

        switch(activeTab) {
            case 'Posts':
                setModalForm("")      
                break
            case 'Music':
                setModalForm(
                    <MusicPlayerForm 
                    
                    onPopup={ (isPopup) => { setIsPopup(isPopup) } } 
                    onAlert={ (alert) => { setAlertContent(alert)} }
                    onState= { (stateAlert) => { setStateAlert(stateAlert) }}
                    />
                ) 
                break
        }
    }

    const handleOutsideClick = (event) => {
        // Nhấp vào modal
        if (isPopup && event.target.className === 'modal') {
          setIsPopup(false)
        }
    };

    return (
        <div id="profile-app">
            <div className="flex h-100">
                <NavigationBar />

                <div className="flex-1">
                    <Header isSearchBar={true} title="Profile" />

                    <div id="wrap-profile" className="flex h-100">
                        <div id="profile">
                            <InfiniteScroll
                                dataLength={posts.length}

                                next={fetchMoreData}

                                hasMore={hasMore}

                                scrollableTarget="profile"
                            >
                                <div id="user-profile">
                                    <div id="cover-photo">
                                        <div id="change-cover-photo-icon" className="icon">
                                            <span className="material-symbols-outlined">photo_camera</span>
                                        </div>
                                        <div id="edit-profile-icon" className="icon">
                                            <span className="material-symbols-outlined">edit</span>
                                        </div>
                                    </div>
                                    <div id="profile-picture" style={{
                                        backgroundImage: `url("${domain}/users/${user.avatar}")`
                                    }
                                    }>
                                        <div id="change-avatar-icon" className="icon">
                                            <span className="material-symbols-outlined">filter_center_focus</span>
                                        </div>
                                    </div>
                                    <div id="user-info">
                                        <h1>{user.firstName + " " + user.lastName}</h1>
                                        <div id="user-info-job">Developer</div>
                                        <div id="user-info-intro">Hello world , I'm a programer</div>
                                        <div id="user-info-statistical">
                                            <div id="user-info-statistical-posting">
                                                <h1>11</h1>
                                                <span>Posting</span>
                                            </div>
                                            <div id="user-info-statistical-follower">
                                                <h1>11K</h1>
                                                <span>Follower</span>
                                            </div>
                                            <div id="user-info-statistical-following">
                                                <h1>100</h1>
                                                <span>Following</span>
                                            </div>
                                        </div>
                                        <div id="user-info-btn">
                                            <span id="user-info-btn-editProfile">Edit Profile</span>
                                            <span id="user-info-btn-shareProfile">Share Profile</span>
                                        </div>

                                        {/* NAVIGATION */}
                                        <div id="navigation" className="flex">
                                            <p className={activeTab === 'Posts' ? 'text-actived' : ''} onClick={() => handleNavigation('Posts')}>Posts</p>
                                            <p className={activeTab === 'Music' ? 'text-actived' : ''} onClick={() => handleNavigation('Music')}>Music</p>
                                            <p className={activeTab === 'Stories' ? 'text-actived' : ''} onClick={() => handleNavigation('Stories')}>Stories</p>
                                            <p className={activeTab === 'Tagged' ? 'text-actived' : ''} onClick={() => handleNavigation('Tagged')}>Tagged</p>
                                        </div>

                                        <span className="material-symbols-outlined pointer" onClick={handleUpload}>
                                            upload
                                        </span>

                                        <div id="content">
                                            {
                                                posts.map((post, index) => (

                                                    <PostProfile
                                                        key={index}

                                                        _id={post._id}

                                                        image={post.image}

                                                        title={post.content}

                                                        totalEmoji={post.loves}

                                                        date={post.creationTime}
                                                       // totalComment={value.totalComment}
                                                    />
                                                ))
                                            }
                                        </div>
                                    </div>
                                </div>
                            </InfiniteScroll>

                            <MusicPlayerBar />
                        </div>
                    </div>
                </div>

            </div>

            <div className={isPopup ? 'd-block' : 'd-none'}>
                <div className="modal" onClick={handleOutsideClick}>{modalForm}</div>
            </div>

            {/* ALERT FORM */}
            <AlertForm 
                stateAlert={stateAlert}
                setStateAlert={(stateAlert) => { setStateAlert(stateAlert) }} 
                code={alertContent.code} 
                message={alertContent.message} 
            />
        </div>
    );
}
