import { Link, useLocation } from 'react-router-dom';
import { useTheme } from '../contexts/ThemeContext';
import { useEffect } from 'react';

export default function NavigationBar() {
    const { theme, toggleTheme, themeObject } = useTheme();
    const location = useLocation();

    function signOut() {
        localStorage.clear();
        window.location.href = '/login';
    }

    useEffect(() => {
        // Kiểm tra xem URL có phải là "/p/:id" không
        if (location.pathname.match(/^\/p\/\d+$/)) {
          alert(222)
        }
      }, [location.pathname]);

    return (
        <div id="vertical-navigation-bar" className="bg-white">
            <div className='flex-1'>
                {/* GROUP ITEM */}
                <div className="mt-25">
                    <b className="heading ml-13">Menu</b>
                    <ul className="list-item">
                        <Link to="/">
                            <li className="row">
                                <img
                                    className="icon-heading"
                                    src="https://img.icons8.com/doodle/48/home--v1.png"
                                    alt="home--v1"
                                />
                                <p className="ml-10">Homepage</p>

                            </li>
                        </Link>

                        <Link to="/explore">
                            <li className="row">

                                <img className="icon-heading" src="https://img.icons8.com/dusk/64/compass--v1.png" alt="compass--v1" />
                                <p className="ml-10">Explore</p>

                            </li>
                        </Link>

                        <Link to="/profile">
                            <li className="row">
                                <img className="icon-heading" src="https://img.icons8.com/windows/32/gender-neutral-user.png" alt="gender-neutral-user" />
                                <p className="ml-10">Profile</p>
                            </li>
                        </Link>

                        <Link to="/chat">
                            <li className="row">
                                <img
                                    className="icon-heading"
                                    src="https://img.icons8.com/pulsar-color/48/chat-message.png"
                                    alt="chat-message"
                                />
                                <p className="ml-10">Chat</p>
                            </li>
                        </Link>
                    </ul>
                </div>
            </div>

            <button style={themeObject.button} className='btn btn-blue' onClick={signOut}>Đăng xuất</button>
        </div>
    )
}
