import axios from 'axios';

const ACCOUNT_ENDPOINT = process.env.REACT_APP_SERVER_SERVICE + "/api/v1/accounts";

export const register = async (account) => {
    try {
        let res = await axios.post(ACCOUNT_ENDPOINT, account);
        return res.data;
    } catch (err) {
        console.log(err);
    }
}

export const login = async (account) => {
    try {
        let res = await axios.get(`${ACCOUNT_ENDPOINT}/login`, account);
        return res.data.token;
    } catch (err) {
        console.log(err);
    }
}

export const logout = async (account) => {
    try {
        let res = await axios.get(`${ACCOUNT_ENDPOINT}/logout`, account);
        return res.data.token;
    } catch (err) {
        console.log(err);
    }
}
