import { useEffect, useRef } from "react"
import "../styles/alertForm.css"

/**
 * Form for alert
 * @param {void} setStateAlert Set status alert
 * @param {bool} isAlert Status alert, true: show alert, otherwise: hidden alert
 * @param {int} code Code
 * @param {string} message Message alert
 */
export default function AlertForm({setStateAlert, stateAlert, code, message}) {
    const timeHiddenRef = useRef(2000) // miliseconds
    const alertFormRef = useRef()

    useEffect(() => {

        if (stateAlert) {
            setTimeout(() => {

                alertFormRef.current.classList.replace("d-block", "d-none")
                
                setStateAlert(false)

            }, timeHiddenRef.current)
        }
    }, [stateAlert])

    return (
        <div ref={alertFormRef} id="alertForm" className={ (stateAlert) ? "d-block" : "d-none" }>

        </div>
    )
}
