import React, { createContext, useContext, useRef } from 'react';

class Theme {
  constructor() {
    this.theme = 'light';
  }
}

const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const themeContextRef = useRef(new Theme());

  return (
    <ThemeContext.Provider value={themeContextRef.current}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useThemeAppContext = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useThemeAppContext must be used within an ThemeProvider');
  }
  return context;
};
