import '../styles/postcard.css'
import { useAudio } from '../contexts/AudioContext'
import { useEffect } from 'react'

export default function PostMusicCard({ card }) {
    const { _id, name, author, file, albumCover, albumId } = card
    const domain = `${process.env.REACT_APP_SERVER_SERVICE}/musics/${albumId}`
    const { audio, setAudio } = useAudio();

    useEffect(() => {
        if (audio) {
            // Pause the current audio, set the new source, load, and play
            audio.pause();
            audio.src = `${domain}/${file}`;
            audio.load();
            audio.play();
        }
    }, [audio, domain, file]);

    useEffect(() => {
        // Cleanup when component unmounts or when a new audio is set
        return () => {
            if (audio) {
                audio.pause();
                setAudio(null);
            }
        };
    }, [audio, setAudio]);

    function playAudio() {
        let newAudio = new Audio(`${domain}/${file}`);
        setAudio(newAudio);
    }

    return (
        <div id={_id} className="postcard" onClick={playAudio}>
            {
                (albumCover !== "")
                    ?
                    <div className="image">
                        <img src={`${domain}/${albumCover}`} alt="post" />
                    </div>
                    :
                    <div className="image">
                        <img src={`${domain}/posts/no-image.jpg`} alt="post" />
                    </div>
            }

            <div className="content">
                <h3 className='name'>{name}</h3>
                <p className='author'>{author}</p>
            </div>

            <div className='flex justify-space-between mt-15'>
                <div className="flex">
                    <div className='flex align-items-center'>
                        <span className={`material-symbols-outlined shape-effect__love`}>favorite</span>
                        <span className='flex align-items-center ml-5'>0</span>
                    </div>
                    <div className='flex align-items-center ml-15'>
                        <i className="fa-regular fa-comment"></i>
                        <span className='ml-5'>0</span>
                    </div>
                </div>

                <div className="flex">
                    <span className="material-symbols-outlined">download</span>
                </div>
            </div>
        </div>
    )
}
