import base64 from 'base-64'
import utf8 from 'utf8'

const Crypto = {
    decrypt: function (encoded) {
        const weight = 4;
  
        // Swap letter at number to last index
        encoded = swap(encoded, encoded.length - 1, weight);
  
        // Remove draft base64 string
        encoded = encoded.substring(0, encoded.length / 2);
        let bytes = base64.decode(encoded);
        let decoded = utf8.decode(bytes);
  
        return decoded;
    }
}

export default Crypto
