import { useEffect, useState } from "react"
import "../styles/musicPlayerBar.css"
import { useAudioContext } from '../contexts/AudioContext'

export default function MusicPlayerBar() {
    const audioContext = useAudioContext();
    const [song, setSong] = useState(null);

    useEffect(() => {
        setSong(audioContext.getSong());
    }, [activeTab])

    return (
        <div id="musicPlayerBar">
            <div>
               <img id="albumCover" src={(song) ? song.albumCover : "" }></img>
                <b>{(song) ? song.name : "" }</b>
                <p>{(song) ? song.author : ""}</p>
            </div>
            <img className="icon" width="30" height="30" src="https://img.icons8.com/ios-filled/50/up-squared.png" alt="up-squared"/>
            <img className="icon" width="30" height="30" src="https://img.icons8.com/ios-glyphs/30/play--v1.png" alt="play--v1"/>
            <img className="icon" width="30" height="30" src="https://img.icons8.com/ios-filled/50/down-squared.png" alt="down-squared"/>
            <img className="icon" width="30" height="30" src="https://img.icons8.com/ios-filled/50/medium-volume--v1.png" alt="medium-volume--v1"/>
        </div>
    )
}
