import axios from 'axios';

const endpoint = `${process.env.REACT_APP_SERVER_SERVICE}/api/users`;

class UserService {

    static async getUsers() {
        try {
            const response = await axios.get(`${endpoint}`);
            return response.data;
        } catch (error) {
            console.log(error);
        }
    }

    static async getUser(userId) {
        try {
            const response = await axios.get(`${endpoint}/${userId}`);
            return response.data;
        } catch (error) {
            console.log(error);
        }
    }

    static async updateUser(userId, userProfile, avatar) {
        try {
            const data = new FormData();
            data.append("userProfile", userProfile);
            data.append("avatar", avatar);
            
            const response = await axios.put(`${endpoint}/${userId}`, data);
            return response.data;
        } catch (error) {
            console.log(error);
        }
    }
}

export default UserService
