import { useContext, useState, useRef, useEffect } from "react";
import { UserContext } from "../contexts/UserContext";
import AvatarEditor from 'react-avatar-editor'
import Dropzone from 'react-dropzone'
import UserService from "../services/user.service";

/**
 * ProfileForm (component)
 * @param {object}
 * @param {object}
 * @param {function}
 */
export default function ProfileForm() {
    const user = useContext(UserContext);
    const sizeAvatar = 150;
    const domain = "http://localhost:8030/users/";
    const editorRef = useRef(null);

    const [firstName, setFirstName] = useState(user.firstName);
    const [isFirstNameValid, setIsFirstNameValid] = useState(true);

    const [lastName, setLastName] = useState(user.lastName);
    const [isLastNameValid, setIsLastNameValid] = useState(true);

    const [avatar, setAvatar] = useState(domain + user._id + "/" + user.avatar);
    const [isAvatarValid, setIsAvatarValid] = useState(true);

    const [bio, setBio] = useState("");
    const [isBioValid, setIsBioValid] = useState(true);

    function handleChange(e) {
        switch (e.target.name) {
            case "firstName":
                setFirstName(e.target.value);
                break;
            case "lastName":
                setLastName(e.target.value);
                break;
            case "bio":
                setBio(e.target.value);
                break;
            default:
                break;
        }
    }

    const handleDrop = (dropped) => {
        const droppedFile = dropped[0];
        setAvatar(droppedFile); 
    };

    function canvasToFile(canvas, fileName, fileType, quality = 1) {
        return new Promise((resolve) => {
          canvas.toBlob(
            (blob) => {
              const file = new File([blob], fileName, { type: fileType, lastModified: Date.now() });
              resolve(file);
            },
            fileType,
            quality
          );
        });
    }

    async function updateProfile(e) {
        let data;
        
        switch (e.target.name) {
            case "firstName":
                data = { firstName };
                break;
            case "lastName":
                data = { lastName };
                break;
            case "bio":
                data = { bio };
                break;
        }

        let payload = ""
        if (e.target.name == "avatar") {
            let img = new Image();
img.crossOrigin = 'anonymous';  // This enables CORS
img.onload = function() {
    let canvas = document.getElementById('myCanvas');
    let ctx = canvas.getContext('2d');
    ctx.drawImage(img, 0, 0);
};
img.src = avatar;
console.log(img)
            // If you want the image resized to the canvas size (also a HTMLCanvasElement)
         //   const canvasScaled = editorRef.current.getImageScaledToCanvas()
          //  const file = await canvasToFile(canvasScaled, "avatar", "image/jpeg", 1)
            
           // payload = await UserService.updateAvatar(user._id, file)
        } else {
            payload = await UserService.updateUser(user._id, data)
        }
        
        if (payload.code == 200) {
            e.target.classList.add("btn-success");
            e.target.innerHTML = "Thành công";

            setTimeout(() => {
                resetButton(e, "btn-success", "Thay đổi")
            }, 2000)
        } else if (payload.code == 500) {
            e.target.classList.add("btn-danger");
            e.target.innerHTML = "Thất bại";

            setTimeout(() => {
                resetButton(e, "btn-danger", "Thay đổi")
            }, 2000)
        }
    }

    function resetButton(e, classRemoved, defaultText) {
        e.target.classList.remove(classRemoved);
        e.target.innerHTML = defaultText;
    }

    return (
        <div className="modalForm">
            <h2>Cập nhật trang cá nhân</h2>

            <div>
                <div className="flex">
                    <span className="step">1</span>
                    <p>Thay đổi họ</p>
                </div>
                <div className="row justify-content-space-between">
                    <div>
                        <input maxLength={24} type="text" name="firstName" placeholder="Ex: Điều cha chưa nói" value={firstName} onChange={(e) => handleChange(e)} />
                        <label className={`mt-5 ${isFirstNameValid ? 'd-none' : 'd-block'}`}>⚠️ Vui lòng nhập họ hợp lệ</label>
                    </div>
                    <button className="btn" onClick={(e) => { updateProfile(e) }} name="firstName">Thay đổi</button>
                </div>
            </div>

            <div>
                <div className="flex">
                    <span className="step">2</span>
                    <p>Thay đổi tên</p>
                </div>
                <div className="row justify-content-space-between">
                    <div>
                        <input maxLength={24} type="text" name="lastName" placeholder="Ex: Điều cha chưa nói" value={lastName} onChange={(e) => handleChange(e)} />
                        <label className={`mt-5 ${isLastNameValid ? 'd-none' : 'd-block'}`}>⚠️ Vui lòng nhập họ hợp lệ</label>
                    </div>
                    <button className="btn" onClick={(e) => { updateProfile(e) }} name="lastName">Thay đổi</button>
                </div>
            </div>

            <div>
                <div className="flex">
                    <span className="step">3</span>
                    <p>Bio (tiểu sử)</p>
                </div>
                <div className="row justify-content-space-between">
                    <div style={
                        {
                            width: "60%"
                        }
                    }>
                        <input maxLength={120} type="text" name="bio" placeholder="Ex: Điều cha chưa nói" value={bio} onChange={(e) => handleChange(e)} />
                        <label className={`mt-5 ${isBioValid ? 'd-none' : 'd-block'}`}>⚠️ Vui lòng nhập họ hợp lệ</label>
                    </div>
                    <button className="btn" onClick={(e) => { updateProfile(e) }} name="bio">Thay đổi</button>
                </div>
            </div>

            <div>
                <div className="flex">
                    <span className="step">4</span>
                    <p>Đổi ảnh đại diện (Bạn có thể kéo thả ảnh vào đây)</p>
                </div>
                <div id="preview-avatar" className="mt-5">

                    {/* <img width="100%" height="100%" src="http://localhost:8030/users/default.jpeg"></img> */}
                    <Dropzone
                        onDrop={handleDrop}
                        noClick
                        noKeyboard
                        style={{ width: sizeAvatar + "px", height: sizeAvatar + "px" }}
                    >
                        {({ getRootProps, getInputProps }) => (
                            <div {...getRootProps()}>
                                <AvatarEditor 
                                ref={editorRef}
                                width={sizeAvatar} height={sizeAvatar} image={avatar} borderRadius={100} />
                                <input {...getInputProps()} />
                            </div>
                        )}
                    </Dropzone>
                </div>
                <label className={`mt-5 ${isAvatarValid ? 'd-none' : 'd-block'}`}>⚠️ Ảnh chứa nội dung không lành mạnh</label>
                <button className="btn mt-5" onClick={(e) => { updateProfile(e) }} name="avatar">Thay đổi ảnh đại diện</button>
            </div>
        </div>
    )
}
