import axios from 'axios';

const endpoint = `${process.env.REACT_APP_SERVER_SERVICE}/api/posts`;

export const createPost = async (content, data) => {
    try {
        const form = new FormData()
        form.append('post', JSON.stringify(content))
        form.append('file', data)
        
        const response = await axios.post(endpoint, form, {
            headers: {
                'Content-Type': 'multipart/form-data',
                'access_token': localStorage.getItem('access_token')
            },
        })

        return response.data;
       
    } catch (error) {
        console.error(error);
    }
}

export const getPosts = async (limit, offset) => {
    try {
        const response = await axios.get(endpoint, {
            params: {
                limit,
                offset
            },
            headers: {
                'access_token': localStorage.getItem('access_token')
            }
        })

        return response.data;
       
    } catch (error) {
        console.error(error);
    }
}

export const deletePost = async (postId) => {
    try {
        const response = await axios.delete(`${endpoint}/${postId}`, {
            headers: {
                'access_token': localStorage.getItem('access_token')
            }
        })

        return response.data;
       
    } catch (error) {
        console.error(error);
    }
}

export const getPost = async (postId) => {
    try {
        const response = await axios.get(`${endpoint}/${postId}`, {
            headers: {
                'access_token': localStorage.getItem('access_token')
            }
        })

        return response.data;
       
    } catch (error) {
        console.error(error);
    }
}

// get post by user id with limit and offet
export const getPostByUserId = async (userId, limit, offset) => {
    try {
        const response = await axios.get(`${endpoint}/user/${userId}`, {
            params: {
                limit,
                offset
            },
            headers: {
                'access_token': localStorage.getItem('access_token')
            }
        })

        return response.data;
       
    } catch (error) {
        console.error(error);
    }
}
