import TextareaAutosize from 'react-textarea-autosize';
import React, { useEffect, useState, useContext, useRef } from 'react';
import Resizer from 'react-image-file-resizer';
import { UserContext } from '../contexts/UserContext';
import { createPost } from '../services/post.service';
import YoutubeEmbed from './YoutubeEmbed';

export default function HandlePost() {
    const user = useContext(UserContext);
    const domain = process.env.REACT_APP_SERVER_SERVICE;

    const [isValidContent, setIsValidContent] = useState(false)     // Check content of post is valid
    const [postContent, setPostContent] = useState("")              // content of post
    const [embedId, setEmbedId] = useState("")                      // id of video youtube

    const boxReviewRef = useRef()
    const imageInputRef = useRef()
    const videoInputRef = useRef()
    const videoSourceRef = useRef()                                  // Chứa blob của video
    const isActive = useRef(false)                                   // Trạng thái kích hoạt của box review, nếu true nghĩa là đang có file(image, video) đang được hiển thị
    const mediaRef = useRef()                                        // Media dùng để đưa sang server (có thể là ảnh, video, link youtube)

    useEffect(() => {
        if (mediaRef.current || postContent !== "") {
            setIsValidContent(true)
        } else {
            setIsValidContent(false)
        }

    }, [postContent, mediaRef.current])

    useEffect(() => {
        // Recogine link youtube
        const regex = /https:\/\/www\.youtube\.com\/watch\?v=([a-zA-Z0-9_-]{11})/g

        if (postContent.match(regex)) {
            // Cho toàn bộ sự kiện khi click vào nút tải ảnh, tải video biêt đang có 1 file đang được hiển thị
            isActive.current = true

            const videoId = postContent.match(regex)[0].split('=')[1]
            showBoxReview()
            setEmbedId(videoId)
        }

    }, [postContent])

    const resizeFile = (file) => {
        return new Promise(resolve => {
            Resizer.imageFileResizer(file, 1920, 1080, 'JPEG', 100, 0,
                uri => {
                    resolve(uri);
                }, 'file');
        })
    };

    function hiddenBoxReview() {
        boxReviewRef.current.classList.remove('boxReview-image')
        boxReviewRef.current.classList.replace('d-block', 'd-none')
    }

    function showBoxReview() {
        boxReviewRef.current.classList.add('boxReview-image')
        boxReviewRef.current.classList.replace('d-none', 'd-block')
    }

    function handleRemoveMedia() {
        // Cho phép tải file mới
        isActive.current = false
        mediaRef.current = null
        hiddenBoxReview()
    }

    function handleRemoveYoutube() {
        // Cho phép tải file mới
        isActive.current = false
        setEmbedId("")
        hiddenBoxReview()
    }

    async function handleImageChange(event) {
        const file = event.target.files[0]

        if (file) {
            // Cho toàn bộ sự kiện khi click vào nút tải ảnh, tải video biêt đang có 1 file đang được hiển thị
            isActive.current = true

            // Return file object
            let image = await resizeFile(file);

            // Show review picture
            showBoxReview()
            boxReviewRef.current.style.backgroundImage = `url(${URL.createObjectURL(image)})`;

            // Render with url
            mediaRef.current = file
        }
    };

    function handleVideoChange(event) {
        const file = event.target.files[0]
        console.log("Videp", file)

        if (file) {
            // Cho toàn bộ sự kiện khi click vào nút tải ảnh, tải video biêt đang có 1 file đang được hiển thị
            isActive.current = true
            
            // Show review picture, mặc định div đang được ẩn đi
            showBoxReview()

            // Render
            if (videoSourceRef.current) {
                videoSourceRef.current.src = URL.createObjectURL(file)
            }

            mediaRef.current = file
        }
    }

    function handleMessageChange(e) {
        setPostContent(e.target.value)
    }

    async function handleUpload() {
        if (isValidContent) {
            const post = {
                "owner": user._id,
                "content": postContent,
                "creationTime": new Date(),
                "loves": 0
            }

            // Reset form
            setPostContent("")
            handleRemoveMedia()

            if (mediaRef.current) {
                const payload = await createPost(post, mediaRef.current)
            } else {
                const payload = await createPost(post, null)
            }   
        }
    }

    return (
        <div className="handle-post bg-white pad-10">
            <div>
                <img
                    className="avatar"
                    src={`${domain}/users/${user._id}/${user.avatar}`}
                    alt="avatar"
                />

                <TextareaAutosize
                    id="post-content"
                    className="ml-13"
                    placeholder="What's on your mind?"
                    name=""
                    value={postContent}
                    onChange={handleMessageChange}
                />

            </div>

            {/* PREVIEW PICTURE OR VIDEO */}
            <div id="js-review" className="mt-25 d-none" ref={boxReviewRef}>
                {
                    (embedId) 
                    ?
                        <>
                            <YoutubeEmbed embedId={embedId} />
                            <i onClick={handleRemoveYoutube} className="fa-solid fa-x"></i>
                        </>
                    :
                    (videoSourceRef.current != "")
                    ?
                        <>
                            <video ref={videoSourceRef} controls>
                                <source type="video/mp4" />
                                Your browser does not support the video tag.
                            </video>

                            <i onClick={handleRemoveMedia} className="fa-solid fa-x"></i>
                        </>
                    :
                        <>
                            <div className="photo" />
                            <i onClick={handleRemoveMedia} className="fa-solid fa-x"></i>
                        </>
                }

            </div>

            <div className="row mt-15 space-between">
                <ul className="list-icon row">
                    <li>
                        <img
                            className="icon-heading"
                            src="https://img.icons8.com/pulsar-color/48/pictures-folder.png"
                            alt="pictures-folder"
                            onClick={(e) => !isActive.current && imageInputRef.current.click()}
                            disabled="true"
                        />

                        <input
                            type="file"
                            accept="image/*"
                            style={{ display: 'none' }}
                            onChange={handleImageChange}
                            ref={imageInputRef}
                            multiple
                            name='image'
                        />
                    </li>
                    <li>
                        <img
                            className="icon-heading"
                            src="https://img.icons8.com/pulsar-color/48/video.png"
                            onClick={(e) => !isActive.current && videoInputRef.current.click()}
                            alt="video"
                            disabled="true"
                        />

                        <input
                            type="file"
                            accept=".mp4, .mkv, .avi, .mov, .flv, .wmv, .webm"
                            style={{ display: 'none' }}
                            onChange={handleVideoChange}
                            ref={videoInputRef}
                            multiple
                            name='video'
                        />
                    </li>
                </ul>
                <div onClick={handleUpload} id="js-btn-post" className={`btn ${(isValidContent) ? 'btn-blue' : 'btn-hidden'
                    }`}>
                    Đăng bài
                </div>
            </div>
        </div>
    )
}
