import { useEffect, useState, useContext } from "react"
import { UserContext } from "../contexts/UserContext"
/**
* Message component = 1 message
* @param {Number} owner
* @param {Object} chat
* @param {Number} currentUser
*/
export default function Message({ message }) {
    const domain = process.env.REACT_APP_SERVER_SERVICE;
    const user = useContext(UserContext);
    const isOwner = senderId === user._id;
    const ownerClass = isOwner ? "comment-right" : "comment-left";
  
    return (
      <div className={`comment ${ownerClass}`}>
        {isOwner || (
          <img
            className="avatar"
            src={`${domain}/users/${receiver.avatar}`}
            alt="avatar"
          />
        )}
        <div className="box">
          {isOwner || <b className="sender">{receiver.firstName} {receiver.lastName}</b>}
          <p className="text">{message.content}</p>
        </div>
      </div>
    );
  }
