import axios from 'axios'

const endpoint = `${process.env.REACT_APP_SERVER_SERVICE}/api/musics`;

class MusicService {
  static async createMusic(infoSong, file) {
    try {
      const formData = new FormData()
      formData.append('file', file)
      formData.append('infoSong', infoSong)

      const response = await axios.post(`${endpoint}`, formData)
      return response.data
    } catch (error) {
      console.log(error)
    }
  }

  static async getMusics() {
    try {
      const response = await axios.get(`${endpoint}`)
      return response.data
    } catch (error) {
      console.log(error)
    }
  }

  static async deleteMusic(musicId) {
    try {
      const response = await axios.delete(`${endpoint}/${musicId}`)
      return response.data
    } catch (error) {
      console.log(error)
    }
  }
}

export default MusicService
