import React, { useState, useEffect, useRef } from "react";
import PostComponent from "./Post";
import { getPosts } from "../services/post.service";

import InfiniteScroll from "react-infinite-scroll-component";
import "../socket/socket-client";

export default function ListPost() {
    const domain = process.env.REACT_APP_SERVER_SERVICE;
    const [post, setPost] = useState([]);
    const [hasMore, setHasMore] = useState(false)
   
    const maxPost = useRef(0);
    const limit = 5;
    const offset = 0;

    useEffect(() => {
        fetchMoreData()

    }, [])

    async function fetchMoreData() {
        const payload = await getPosts(limit, offset + maxPost.current)
        console.log(payload)
    }

    return (
        <InfiniteScroll
            dataLength={post.length}
            next={fetchMoreData}
            hasMore={hasMore}
            loader={<h4>Loading...</h4>}
            scrollableTarget="content"
        >
            {
                post.map((item, index) => (
                    <PostComponent
                        key = {index}

                        _id = {item._id}

                        sender = {item.sender}

                        content = {{
                            text: item.content.text,
                            image: `${domain}/uploads/${item.content.image}`,
                        }}

                        creationTime={item.creationTime}

                        comments = {item.comments}

                        post = {post}

                        setPost = {setPost}

                        loves = {item.loves}                          // Tổng số lượt tương tác bài viết

                        isLove = {item.isLove}                        // Trạng thái tương tác
                    />
                ))
            }
        </InfiniteScroll>
    );
}
