import axios from 'axios';

const ACCOUNT_ENDPOINT = process.env.REACT_APP_SERVER_SERVICE + "/api/v1";

export const register = async (account) => {
    try {
        let res = await axios.post(`/api/v1/register`, account);
        return res.data;
    } catch (err) {
        console.log(err);
    }
}

export const login = async (account) => {
    try {
        let res = await axios.post(`/api/v1/login`, account);
        return res.data.token;
    } catch (err) {
        console.log(err);
    }
}

export const logout = async (account) => {
    try {
        let res = await axios.get(`/api/v1/logout`, account);
        return res.data.token;
    } catch (err) {
        console.log(err);
    }
}
