import { useContext, useState } from "react";
import MusicService from "../services/music.service";
import { UserContext } from "../contexts/UserContext";
import Resizer from 'react-image-file-resizer';

/**
 * Notifications (warnings)
 * @param {object} onPopup true: MusicPlayerForm will be show, otherwise
 * @param {object} onAlert { code, message }
 * @param {function} onState true (show alert) or false (hidden alert)
 */
export default function ProfileForm({ onPopup, onAlert, onState }) {
    const [firstName, setFirstName] = useState("");
    const [isFirstNameValid, setIsFirstNameValid] = useState(true);

    const [lastName, setLastName] = useState("");
    const [isLastNameValid, setIsLastNameValid] = useState(true);

    const [avatar, setAvatar] = useState(null);
    const [isAvatarValid, setIsAvatarValid] = useState(true);

    const [bio, setBio] = useState("");
    const [isBioValid, setIsBioValid] = useState(true);

    function handleChange(e) {
        switch (e.target.name) {
            case "firstName":
                setFirstName(e.target.value);
                break;
            case "lastName":
                setLastName(e.target.value);
                break;
            case "avatar":
                setAvatar(e.target.files[0]);
                break;
            case "bio":
                setBio(e.target.value);
                break;
            default:
                break;
        }
    }

    function handleImageChange() {

    }

    return (
        <div className="modalForm">
            <h2>Cập nhật trang cá nhân</h2>

            <div>
                <div className="flex">
                    <span className="step">1</span>
                    <p>Thay đổi họ</p>
                </div>
                <div className="row justify-content-space-between">
                    <div>
                        <input maxLength={24} type="text" name="firstName" placeholder="Ex: Điều cha chưa nói" value={firstName} onChange={(e) => handleChange(e)} />
                        <label className={`mt-5 ${isFirstNameValid ? 'd-none' : 'd-block'}`}>⚠️ Vui lòng nhập họ hợp lệ</label>
                    </div>
                    <button className="btn">Thay đổi</button>
                </div>
            </div>

            <div>
                <div className="flex">
                    <span className="step">1</span>
                    <p>Thay đổi tên</p>
                </div>
                <div className="row justify-content-space-between">
                    <div>
                        <input maxLength={24} type="text" name="firstName" placeholder="Ex: Điều cha chưa nói" value={firstName} onChange={(e) => handleChange(e)} />
                        <label className={`mt-5 ${isFirstNameValid ? 'd-none' : 'd-block'}`}>⚠️ Vui lòng nhập họ hợp lệ</label>
                    </div>
                    <button className="btn">Thay đổi</button>
                </div>
            </div>

            <div>
                <div className="flex">
                    <span className="step">3</span>
                    <p>Bio (Tiểu sử)</p>
                </div>
                <input maxLength={24} type="text" placeholder="Ex: Ali Hoàng Dương" value={lastName}  onChange={(e) => handleChange(e)} />
                <label className={`mt-5 ${isLastNameValid ? 'd-none' : 'd-block'}`}>⚠️ Vui lòng nhập bio hợp lệ</label>
            </div>

            <div>
                <div className="flex">
                    <span className="step">4</span>
                    <p>Đổi ảnh đại diện</p>
                </div>
                <div id="preview-avatar" className="mt-5">
                    <img width="100%" height="100%" src="http://localhost:8030/users/default.jpeg"></img>
                </div>
                <div className="uploadButton">
                    <input
                        type="file"
                        id="actual-btn-image"
                        hidden
                        onChange={handleImageChange}
                        accept="image/png, image/gif, image/jpeg"
                    />
                    <label htmlFor="actual-btn-image">Choose File</label>
                </div>
            </div>
        </div>
    )
}
