import { useEffect, useState } from "react"
import MessageService from "../services/message.service"

export default function Notification() {
    const [isShowNotification, setIsShowNotification] = useState(false)
    const domain = process.env.REACT_APP_SERVER_SERVICE;
    const [inbox, setInbox] = useState(null)
    const limit = 7

    useEffect(() => {
        fetchMessages()
    }, [])

    async function fetchMessages() {
        const response = await MessageService.getMessagesByOwner();

        if (response.code === 200) {
            setInbox(response.data)
        }
    }

    function showNotification() {
        setIsShowNotification(!isShowNotification)
    }

    return (
        <div id='notification'>
            <div style={
                {
                    position: "relative"
                }
            }>
                <img onClick={showNotification} className='header-icon' src="https://img.icons8.com/plasticine/100/appointment-reminders.png" alt="appointment-reminders" />
                <span style={
                    {
                        position: 'absolute',
                        width: "10px",
                        height: "10px",
                        borderRadius: "50%",
                        backgroundColor: "red",
                        right: "2px",
                        top: "2px"
                    }
                }></span>
            </div>
            {
                (isShowNotification) ?
                    <ul>
                        <header>
                            <h3>Tin nhắn đến</h3>
                            <a className='notification_actions' style={
                                {
                                    cursor: 'pointer'
                                }
                            }>Đánh dấu đã đọc</a>
                        </header>

                        <div id='notification_content'>
                        {
                            inbox.map((message) => {
                            <li className='notification_actions'>
                                <img className='avatar' src={`${domain}/users/${message.sender._id}/${message.sender.avatar}`}></img>
                                <div>
                                    <b>{message.sender.firstName} {message.sender.lastName}</b>
                                    <p>{message.content}</p>
                                </div>
                            </li>
                            })
                        }
                        </div>

                        <a id='seeAll' href="">Xem tất cả tin nhắn</a>
                    </ul>
                    :
                    null
            }
        </div>
    )
}
