import { useEffect, useRef, useState } from "react"
import { useNavigate } from "react-router-dom"
import { createBrowserHistory } from 'history';

export default function SearchBar({ placeholder }) {
    const [keyword, setKeyWord] = useState("");
    const searchBarRef = useRef();
    const history = createBrowserHistory();
    const navigate = useNavigate();

    searchBarRef.current.addEventListener("keydown", function(event) {
        if (event.key === "Enter") {
            const passedData = {
                "Allo": 123
            }

            /* params url
                + results: Kết quả trả về 
                + keyword
            */
           console.log(keyword)
            navigate('search', { state: passedData })
            history.push({
                pathname: '/search',
                search: '?q=' + keyword
            })
        }
    })

    return (
        <div className="search-bar">
            <i className="fa-solid fa-magnifying-glass" />
            <input ref={searchBarRef} type="text" name="" id="" placeholder={placeholder} value={keyword} onChange={(e) => { setKeyWord(e.target.value)}}/>
        </div>
    )
}
