import { useState } from "react";
import Resizer from 'react-image-file-resizer';
import MusicService from "../services/music.service.js";
import { useUserContext } from "../contexts/UserContext.js";

export default function YoutubeMusicUploadForm({ onPopup, onAlert, onState }) {
    const [linkYoutube, setLinkYoutube] = useState('');
    const [isLinkValid, setIsLinkValid] = useState(true);

    function handleUploadClick() {

    }
    return (
        <>
            <h2>Add music with youtube</h2>
            <div>
                <div className="flex">
                    <span className="step">1</span>
                    <p>Enter link youtube</p>
                </div>
                <input maxLength={24} type="text" placeholder="Ex: https://www.youtube.com/watch?v=9u1wkvVY7jc" value={linkYoutube} onChange={(e) => setLinkYoutube(e.target.value)} />
                <label className={`mt-5 ${isLinkValid ? 'd-none' : 'd-block'}`}>⚠️ Please enter url valid</label>
            </div>

            <div>
                <div className="flex">
                    <span className="step">2</span>
                    <p>Preview</p>
                </div>
                <iframe width="100%" height="315" src="https://www.youtube.com/embed/9u1wkvVY7jc?si=7Fq4ecGC7JiguIeA" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" allowfullscreen></iframe>
            </div>


            <div className="flex justify-space-center">
                <button className="btn btn-black" onClick={handleUploadClick}>Upload Music</button>
            </div>
        </>
    )
}
