import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import HomePage from "./pages/HomePage.jsx";
import Login from "./pages/LoginPage.jsx"
import Register from "./pages/RegisterPage.jsx"
import Chat from "./pages/ChatPage.jsx";
import Profile from "./pages/ProfilePage.jsx";
import AnotherProfilePage from "./pages/AnotherProfilePage.jsx";
import PrivateRoute from "./config/PrivateRoute.js";
import { useState } from "react";
import { UserProvider } from "./contexts/UserContext.js";
import UserService from "./services/user.service.js";

function App() {
  const [isLoading, setLoading] = useState(false);
  const [userData, setUserData] = useState({});

  const fetchData = async (owner) => {
    try {
      const response = await UserService.getUser(owner);

      if (response.code === 200) {
        setUserData(response.data);
        setLoading(false);
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
      setLoading(false);
    }
  };

  const getCurrentUserId = () => {
    // Implement the logic to get the current user's ID
    return userData.id;
  };

  const ProfileRoute = ({ element: Element, userId }) => {
    const currentUserId = getCurrentUserId();
    const isCurrentUser = userId && userId === currentUserId;
  
    return isCurrentUser ? <Element /> : <Navigate to="/profile" replace />;
  };
  

  return (
    <>
      <UserProvider userData={userData} isLoading={isLoading} setUserData={setUserData}>
        <BrowserRouter>
          <Routes>
            <Route element={<PrivateRoute fetchData={fetchData} />}>
              <Route element={<HomePage />} path="/" />
              <Route element={<Chat />} path="/chat" />
              <Route path="/profile" element={ <Profile /> }>
                <Route path=":userId" element={<ProfileRoute element={<AnotherProfilePage />} />} />
              </Route>
            </Route>

            <Route element={<Login />} path="/login" />
            <Route element={<Register />} path="/register" />
          </Routes>
        </BrowserRouter>
      </UserProvider>
    </>
  );
}

export default App;
