import { UserContext } from '../contexts/UserContext';
import { addReaction, checkReaction } from '../services/reaction.service';
import { deletePost } from '../services/post.service';

export default function Post({ 
    _id, 
    owner, 
    content, 
    creationTime, 
    loves, 
    comments, 
    post, 
    setPost,
    image
}) {
    const domain = process.env.REACT_APP_SERVER_SERVICE
    const user = useContext(UserContext)
    const [existImage, setExistImage] = useState(false)
    const [isActiveTooltip, setIsActiveTooltip] = useState(false)
    const [status, setStatus] = useState(checkReaction(_id, user._id))
