import { useContext, useRef, useState } from "react";
import { UserContext } from "../contexts/UserContext";
import TextareaAutosize from 'react-textarea-autosize';
import CommentService from "../services/comment.service";
import ImageUploadButton from "./ImageUploadButton";

export default function FormComment({ postID }) {
    const domain = process.env.REACT_APP_SERVER_SERVICE;
    const [message, setMessage] = useState("")
    const user = useContext(UserContext);

    const isActive = useRef();
    const [media, setMedia] = useState();

    const handleMessageChange = event => {
        setMessage(event.target.value);
    };

    async function postComment() {
       
        if (isValidComment())
        {
            const comment = {
                owner: user._id,
                postId: postID,
                content: message,
                creationTime: new Date()
            }
            const payload = await CommentService.createComment(comment)
            setMessage("")
        }
    }

    function isValidComment()
    {
        if (message.length == 0)
            return false;
        return true;
    }

    return (
        <div className="form-comment row">
            <img
                className="avatar"
                src={`${domain}/users/${user._id}/${user.avatar}`}
                alt="avatar"
            />
            <div>
                <TextareaAutosize 
                    value={message}
                    className="post-comment ml-13"
                    placeholder="Bạn đang nghĩ gì ?"
                    onChange={handleMessageChange} />
                <div className="icon-comment">
                    <ul className="list-icon row align-item-center">
                        <li>
                            <img
                                id="js-upload-picture"
                                className="icon-heading"
                                src="https://img.icons8.com/external-icongeek26-linear-colour-icongeek26/64/external-Stickers-printing-icongeek26-linear-colour-icongeek26.png"
                                alt="external-Stickers-printing-icongeek26-linear-colour-icongeek26"
                            />
                        </li>
                        <li>
                            <ImageUploadButton isActive={isActive} setMedia={setMedia}  />
                        </li>
                    </ul>
                    <p id="js-btn-post" className="btn btn-blue" onClick={postComment}>
                        Đăng bình luận
                    </p>
                </div>
            </div>
        </div>
    )
}
