import React, { createContext, useContext, useState } from 'react';

export const ModalContext = createContext({});

export const ModalProvider = ({ children }) => {
  /*
    1. Nhận function callback Modal 
    2. Set content modal 
    3. Trạng thái hiển thị
    components = {
      modalForm: any (HTML),
      isPopup: boolean
    }
  */
  const [components, setComponents] = useState({});

  const handleOutsideClick = (event) => {
    // Nhấp vào modal
    if (components.isPopup && event.target.className === 'modal') {
      components.isPopup = false
    }
  };

  return (
    <ModalContext.Provider value={{
      components,
      setComponents
    }}>
      {children}
      <div className={components.isPopup ? 'd-block' : 'd-none'}>
            <div className="modal" onClick={handleOutsideClick}>{components.modalForm}</div>
        </div>
    </ModalContext.Provider>
  );
};

export const useModalContext = () => {
  const context = useContext(ModalContext);
  
  return context;
};
