import "../styles/musicPlayerForm.css"
import UploadButton from "./UploadButton"
import { useState } from "react";

export default function MusicPlayerForm() {
    const [isFileSelected, setIsFileSelected] = useState(false);
    const [nameSong, setNameSong] = useState("")
    const [author, setAuthor] = useState("")

    const handleUploadClick = () => {
        if (!nameSong) {

            return
        }

        if (!author) {
            return
        }

        if (!isFileSelected) {
            return
        }
        
    };

    return (
        <div className="musicPlayerForm">
            <h2>Add music</h2>
            <div>
                <div className="flex">
                    <span className="step">1</span>
                    <p>Enter name song</p>
                </div>
                <input type="text" placeholder="Ex: Điều cha chưa nói" value={nameSong}/>
                <label className="mt-5">⚠️ Please enter name song</label>
            </div>

            <div>
                <div className="flex">
                    <span className="step">2</span>
                    <p>Enter author</p>
                </div>
                <input type="text" placeholder="Ex: Ali Hoàng Dương" value={author}/>
            </div>

            <div>
                <div className="flex">
                    <span className="step">3</span>
                    <p>Upload your song</p>
                </div>
                <UploadButton onFileSelect={(selected) => setIsFileSelected(selected)}  />
            </div>

            <div className="flex justify-space-center">
                <button className="btn btn-black" onClick={handleUploadClick}>Upload Music</button>
            </div>
        </div>
    )
}
