import axios from 'axios';

const REACT_APP_SEVER_IP = process.env.REACT_APP_SEVER_IP;
const REACT_APP_SERVER_PORT = process.env.REACT_APP_SERVER_PORT;
const REACT_APP_API_ADD_MESSAGE = process.env.REACT_APP_API_DELETE_COMMENT;
const REACT_APP_API_GET_MESSAGES = process.env.REACT_APP_API_GET_MESSAGES;

const addMessageEndPoint = `${REACT_APP_SEVER_IP}:${REACT_APP_SERVER_PORT}${REACT_APP_API_ADD_MESSAGE}`;
const getMessagesEndPoint = `${REACT_APP_SEVER_IP}:${REACT_APP_SERVER_PORT}${REACT_APP_API_GET_MESSAGES}`;

const config = {
    headers: {
        'Content-Type': 'application/json'
    }
};

const addMessageAPI = async (message) => {
    try {
        await axios.post(addMessageEndPoint, message);
    } catch (error) {
        console.error('Error: addMessage function at adapters/message.js:', error);
        throw error;
    }
};

const getMessagesAPI = async (owner, friend) => {
    try {
        const response = await axios.post(getMessagesEndPoint, {
            owner: owner,
            friend: friend
        }, config);
   
        return response.data
    } catch (error) {
        console.error('Error: getMessages function at adapters/message.js:', error);
        throw error;
    }
};

export {addMessageAPI, getMessagesAPI}
