import { BrowserRouter, Routes, Route, useParams } from "react-router-dom";
import HomePage from "./pages/HomePage.jsx";
import Login from "./pages/LoginPage.jsx"
import Register from "./pages/RegisterPage.jsx"
import Chat from "./pages/ChatPage.jsx";
import Profile from "./pages/ProfilePage.jsx";
import AnotherProfilePage from "./pages/AnotherProfilePage.jsx";
import PrivateRoute from "./config/PrivateRoute.js";
import { useState } from "react";
import { UserProvider } from "./contexts/UserContext.js";
import UserService from "./services/user.service.js";
import { AudioProvider } from "./contexts/AudioContext.js";
import { ThemeProvider } from "./contexts/ThemeContext.js";

function App() {
  function RenderProfile() {
    const { userId } = useParams();

    if (userId === localStorage.getItem('ownerID')) {
      return <Profile />
    }
    return <AnotherProfilePage />
  }

  return (
    <UserProvider>
      <ThemeProvider>
        <AudioProvider>
          <BrowserRouter>
            <Routes>
              <Route element={<PrivateRoute />}>
                <Route element={<HomePage />} path="/" />
                <Route element={<Chat />} path="/chat" />
                <Route path="/profile">
                  <Route index element={<Profile />} />
                  <Route path=":userId" element={<RenderProfile />} />
                </Route>
              </Route>

              <Route element={<Login />} path="/login" />
              <Route element={<Register />} path="/register" />
            </Routes>
          </BrowserRouter>
        </AudioProvider>
      </ThemeProvider>
    </UserProvider>
  );
}

export default App;
