import axios from 'axios';

const endpoint = `${process.env.REACT_APP_SERVER_SERVICE}/api/users`;

export const getUser = async (token, id) => {
    try {
        let response = await axios.get(`${endpoint}/${id})`);
        return response.data;
    
    } catch (error) {
        console.log(error);
    }
}
