import { useContext, useState } from "react";
import AvatarEditor from 'react-avatar-editor'
import Dropzone from 'react-dropzone'
/**
 * ProfileForm (component)
 * @param {object}
 * @param {object}
 * @param {function}
 */
export default function ProfileForm() {
    const sizeAvatar = 150;

    const [firstName, setFirstName] = useState("");
    const [isFirstNameValid, setIsFirstNameValid] = useState(true);

    const [lastName, setLastName] = useState("");
    const [isLastNameValid, setIsLastNameValid] = useState(true);

    const [avatar, setAvatar] = useState("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSVymUM5aA-ccAI-28sAb5BxGD-JJdp_edwgnms0RmwlQ&s");
    const [isAvatarValid, setIsAvatarValid] = useState(true);

    const [bio, setBio] = useState("");
    const [isBioValid, setIsBioValid] = useState(true);

    function handleChange(e) {
        switch (e.target.name) {
            case "firstName":
                setFirstName(e.target.value);
                break;
            case "lastName":
                setLastName(e.target.value);
                break;
            case "avatar":
                setAvatar(e.target.files[0]);
                break;
            case "bio":
                setBio(e.target.value);
                break;
            default:
                break;
        }
    }

    const handleDrop = (dropped) => {
        const droppedFile = dropped[0];
        setAvatar(droppedFile);

        // Read the content of the dropped file
        const reader = new FileReader();
        reader.onload = () => {
            const fileContent = reader.result;
            // Now you can send fileContent to your server using a fetch or any other method
            // For example, using fetch:
            sendToServer(fileContent);
        };

        // Read the content as a data URL
        reader.readAsDataURL(droppedFile);
    };

    return (
        <div className="modalForm">
            <h2>Cập nhật trang cá nhân</h2>

            <div>
                <div className="flex">
                    <span className="step">1</span>
                    <p>Thay đổi họ</p>
                </div>
                <div className="row justify-content-space-between">
                    <div>
                        <input maxLength={24} type="text" name="firstName" placeholder="Ex: Điều cha chưa nói" value={firstName} onChange={(e) => handleChange(e)} />
                        <label className={`mt-5 ${isFirstNameValid ? 'd-none' : 'd-block'}`}>⚠️ Vui lòng nhập họ hợp lệ</label>
                    </div>
                    <button className="btn">Thay đổi</button>
                </div>
            </div>

            <div>
                <div className="flex">
                    <span className="step">2</span>
                    <p>Thay đổi tên</p>
                </div>
                <div className="row justify-content-space-between">
                    <div>
                        <input maxLength={24} type="text" name="firstName" placeholder="Ex: Điều cha chưa nói" value={lastName} onChange={(e) => handleChange(e)} />
                        <label className={`mt-5 ${isLastNameValid ? 'd-none' : 'd-block'}`}>⚠️ Vui lòng nhập họ hợp lệ</label>
                    </div>
                    <button className="btn">Thay đổi</button>
                </div>
            </div>

            <div>
                <div className="flex">
                    <span className="step">3</span>
                    <p>Bio (tiểu sử)</p>
                </div>
                <div className="row justify-content-space-between">
                    <div style={
                        {
                            width: "60%"
                        }
                    }>
                        <input maxLength={110} type="text" name="firstName" placeholder="Ex: Điều cha chưa nói" value={bio} onChange={(e) => handleChange(e)} />
                        <label className={`mt-5 ${isBioValid ? 'd-none' : 'd-block'}`}>⚠️ Vui lòng nhập họ hợp lệ</label>
                    </div>
                    <button className="btn">Thay đổi</button>
                </div>
            </div>

            <div>
                <div className="flex">
                    <span className="step">4</span>
                    <p>Đổi ảnh đại diện (Bạn có thể kéo thả ảnh vào đây)</p>
                </div>
                <div id="preview-avatar" className="mt-5">

                    {/* <img width="100%" height="100%" src="http://localhost:8030/users/default.jpeg"></img> */}
                    <Dropzone
                        onDrop={handleDrop}
                        noClick
                        noKeyboard
                        style={{ width: sizeAvatar + "px", height: sizeAvatar + "px" }}
                    >
                        {({ getRootProps, getInputProps }) => (
                            <div {...getRootProps()}>
                                <AvatarEditor width={sizeAvatar} height={sizeAvatar} image={avatar} borderRadius={100} />
                                <input {...getInputProps()} />
                            </div>
                        )}
                    </Dropzone>
                </div>
                <label className={`mt-5 ${isAvatarValid ? 'd-none' : 'd-block'}`}>⚠️ Ảnh chứa nội dung không lành mạnh</label>
                <button className="btn mt-5">Thay đổi ảnh đại diện</button>
            </div>
        </div>
    )
}
