import { useEffect, useState } from "react"

const server = process.env.REACT_APP_SEVER_IP + ":" + process.env.REACT_APP_SERVER_PORT

/**
* Message component = 1 message
* @param {Number} owner
* @param {Object} chat
* @param {Number} currentUser
*/
export default function Message({ owner, chat, selectedFriend, currentUser }) {
    const [fullName, setFullName] = useState("")
    const [avatar, setAvatar] = useState("")

    useEffect(() => {
        if (chat.sender._id === currentUser._id) {
            const fullName = currentUser.firstName + " " + currentUser.lastName
            const avatar = currentUser.avatar
            setAvatar(server + "/uploads/" + avatar)
            setFullName(fullName)
        } else {
            const fullName = selectedFriend.friend.firstName + " " + selectedFriend.friend.lastName
            const avatar = selectedFriend.friend.avatar
            setAvatar(server + "/uploads/" + avatar)
            setFullName(fullName)
        }
    }, [])

    return (
        <div className={`comment ${(owner === "other") ? "comment-left" : "comment-right"}`}>
            {
                (owner === "other") 
                ?
                    <img
                        className="avatar"
                        src={avatar}
                        alt="avatar"
                    />
                :
                    ""
            }
            <div className="box">
                {
                    (owner === "other") 
                    ?
                        <b className="sender">{fullName}</b>
                    :
                        ""
                }
                <p className="text">{chat.message.text}</p>
            </div>
        </div>
    )
}
