import { useContext, useState } from "react";
import { UserContext } from "../contexts/UserContext";
import TextareaAutosize from 'react-textarea-autosize';
import CommentService from "../services/comment.service";

export default function FormComment({ postID }) {
    const domain = process.env.REACT_APP_SERVER_SERVICE;
    const [message, setMessage] = useState("")
    const user = useContext(UserContext);

    const handleMessageChange = event => {
        setMessage(event.target.value);
    };

    function postComment() {
       
        if (isValidComment())
        {
            const comment = {
                owner: user._id,
                postId: postID,
                content: message
            }
            const payload = CommentService.createComment(comment)
            console.log(payload)
            setMessage("")
        }
    }

    function isValidComment()
    {
        if (message.length == 0)
            return false;
        return true;
    }

    return (
        <div className="form-comment row">
            <img
                className="avatar"
                src={domain + "/users/" + user.avatar}
                alt="avatar"
            />
            <div>
                <TextareaAutosize 
                    value={message}
                    className="post-comment ml-13"
                    placeholder="Bạn đang nghĩ gì ?"
                    onChange={handleMessageChange} />
                <div className="icon-comment">
                    <ul className="list-icon row align-item-center">
                        <li>
                            <img
                                id="js-upload-picture"
                                className="icon-heading"
                                src="https://img.icons8.com/pulsar-color/48/pictures-folder.png"
                                alt="pictures-folder"
                            />
                        </li>
                        <li>
                            <img
                                className="icon-heading"
                                src="https://img.icons8.com/pulsar-color/48/pictures-folder.png"
                                alt="pictures-folder"
                            />
                        </li>
                    </ul>
                    <p id="js-btn-post" className="btn btn-blue" onClick={postComment}>
                        Đăng bình luận
                    </p>
                </div>
            </div>
        </div>
    )
}
