import React, { useState, useEffect, useRef } from "react";
import PostComponent from "./Post";
import { getPosts } from "../services/post.service";

import InfiniteScroll from "react-infinite-scroll-component";
import socket from "../socket/socket-client";

export default function ListPost() {
    const domain = process.env.REACT_APP_SERVER_SERVICE;
    const [post, setPost] = useState([]);
    const [hasMore, setHasMore] = useState(false)
   
    const maxPost = useRef(0);
    const limit = 6;
    const offset = 0;

    useEffect(() => {
        fetchMoreData()

        socket.on("new-post", (data) => {
            setPost([data, ...post]);
        });
        
        return () => {
            socket.off("new-post");
        };
    }, [])

    async function fetchMoreData() {
        const payload = await getPosts(limit, offset + maxPost.current)

        if (payload.data.length > 0) {
            setPost([...post, ...payload.data])
            maxPost.current += payload.data.length
            setHasMore(true)
        } else {
            setHasMore(false)
        }
    }

    return (
        <InfiniteScroll
            dataLength={post.length}
            next={fetchMoreData}
            hasMore={hasMore}
            loader={<h4>Loading...</h4>}
            scrollableTarget="content"
        >
            {
                post.map((item, index) => (
                    <PostComponent key={index} post={item}/>
                ))
            }
        </InfiniteScroll>
    );
}
