import { useEffect, useState, useContext } from "react"
import { UserContext } from "../contexts/UserContext"
/**
* Message component = 1 message
* @param {Number} owner
* @param {Object} chat
* @param {Number} currentUser
*/
export default function Message({ message }) {
    const domain = process.env.REACT_APP_SERVER_SERVICE
    const user = useContext(UserContext);
    const owner = (message.senderId === user._id) ? "my" : "other"

    return (
        <div className={`comment ${(owner === "other") ? "comment-left" : "comment-right"}`}>
            {
                (owner === "other") ?
                    <img
                        className="avatar"
                        src={`${domain}/users/${message.receiver.avatar}`}
                        alt="avatar"
                    /> : ""
            }
            <div className="box">
                { (owner === "other") ? <b className="sender">{fullName}</b> : "" }
                <p className="text">{chat.message.text}</p>
            </div>
        </div>
    )
}
